 -  working-with-hub-sites-and-search-api    # List all sites being a hub site or associate to a hub site  $results   =   Submit-PnPSearchQuery   -Query   'contentclass=sts_site'   -RefinementFilters   'departmentid:string("{*",linguistics=off)'   -TrimDuplicates   $false   -SelectProperties   @( "Title" , "Path" , "DepartmentId" , "SiteId" )   -All   -RelevantResults  # Filter out the hub sites  $hubSites   =   $results   |?   {   $_ . DepartmentId . Trim ( '{' , '}' )   -eq   $_ . SiteId    }  # Loop over the hub sites  foreach (   $hub   in   $hubSites   )   { 
     Write-Host   $hub . Title   -   $hub . Path   -ForegroundColor   Green 
     # Filter out sites associated to the current hub 
     $associatedSites   =   ( $results   |?   {   $_ . DepartmentId   -eq   $hub . DepartmentId   -and   $_ . SiteId   -ne   $hub . SiteId   }) 
     foreach ( $site   in   $associatedSites )   { 
         Write-Host   " `t " $site . Title   -   $site . Path   -ForegroundColor   Yellow 
     }  }

https://gallery.technet.microsoft.com/office/Upload-Multiple-Documents-4c4aa989   function   UploadDocuments (){  Param ( 
         [ ValidateScript ({ If ( Test-Path   $_ ){ $true } else { Throw   "Invalid path given: $_" }})]  
         $LocalFolderLocation , 
         [String]  
         $siteUrl , 
         [String] 
         $documentLibraryName  )  Process { 
         $path   =   $LocalFolderLocation . TrimEnd ( '\' ) 

         Write-Host   "Provided Site :" $siteUrl   -ForegroundColor   Green 
         Write-Host   "Provided Path :" $path   -ForegroundColor   Green 
         Write-Host   "Provided Document Library name :" $documentLibraryName   -ForegroundColor   Green 

           try { 
                 $credentials   =   Get-Credential 

                 Connect-PnPOnline   -Url   $siteUrl   -CreateDrive   -Credentials   $credentials 

                 $file   =   Get-ChildItem   -Path   $LocalFolderLocation   -Recurse 
                 $i   =   0 ; 
                 Write-Host   "Uploading documents to Site.."   -ForegroundColor   Cyan 
                 ( dir   $path   -Recurse )   |   %{ 
                     try { 
                         $i ++ 
                         if ( $_ . GetType (). Name   -eq   "FileInfo" ){ 
                           $SPFolderName   =    $documentLibraryName   +   $_ . DirectoryName . Substring ( $path . Length ); 
                           $status   =   "Uploading Files :'"   +   $_ . Name   +   "' to Location :"   +   $SPFolderName 
                           Write-Progress   -activity   "Uploading Documents.."   -status   $status   -PercentComplete   (( $i   /   $file . length )    *   100 ) 
                           $te   =   Add-PnPFile   -Path   $_ . FullName   -Folder   $SPFolderName 
                          }           
                         } 
                     catch { 
                     } 
                  } 
             } 
             catch { 
              Write-Host   $_ . Exception . Message   -ForegroundColor   Red 
             } 

   }  }  #UploadDocuments -LocalFolderLocation {Local Folder Location} -siteUrl {Site collection URL} -documentLibraryName {Document Library Name}
